mapper.update:
UPDATE TBL_REPLY 
SET REPLY= #{reply}, UPDATEDATE = SYSDATE
WHERE RNO = #{rno}

mapper.select:



SELECT RNO, REPLY, REPLIER, REPLYDATE, UPDATEDATE
FROM
	(
		SELECT /*+ INDEX_DESC(TBL_REPLY PK_REPLY) */ 
		ROWNUM RN, RNO, REPLY, REPLIER, REPLYDATE, UPDATEDATE
		FROM TBL_REPLY
		WHERE BNO = #{bno}
		AND RNO > 0 AND ROWNUM <= #{cri.pageNum} * #{cri.amount}
	)
WHERE RN > (#{cri.pageNum}	- 1) * #{cri.amount}
